import { useState } from "react";
import { Header } from "@/components/layout/header";
import { DocumentCard } from "@/components/case/document-card";
import { TimelineEventComponent } from "@/components/case/timeline-event";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Checkbox } from "@/components/ui/checkbox";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Label } from "@/components/ui/label";
import { Search, Filter, X, FileText, Clock, Calendar, Tags, MapPin } from "lucide-react";
import { useCaseSearch } from "@/hooks/use-case-search";
import { CASE_DOCUMENTS, CASE_TIMELINE, Doc, TimelineEvent, formatDate, getDocumentById } from "@/lib/case-data";

export default function SearchPage() {
  const [selectedDocument, setSelectedDocument] = useState<Doc | null>(null);
  const [advancedFilters, setAdvancedFilters] = useState({
    dateFrom: "",
    dateTo: "",
    tags: [] as string[],
    locations: [] as string[],
    hasEvidence: false,
    hasFOIA: false,
  });
  const [showAdvanced, setShowAdvanced] = useState(false);
  const [sortBy, setSortBy] = useState("relevance");

  const { query, setQuery, results, activeFilters, addFilter, removeFilter, clearFilters } = useCaseSearch();

  const handleSearch = (searchQuery: string) => {
    setQuery(searchQuery);
  };

  const handleExport = () => {
    window.print();
  };

  const handleDocumentClick = (docId: string) => {
    const doc = getDocumentById(docId);
    if (doc) {
      setSelectedDocument(doc);
    }
  };

  // Get unique tags and locations for filters
  const allTags = Array.from(new Set([
    ...results.flatMap(item => item.tags || [])
  ])).sort();

  const allLocations = Array.from(new Set([
    "Lafayette, LA",
    "Mississippi",
    "Texas", 
    "Utah",
    "Washington DC",
    "Federal Court"
  ])).sort();

  // Filter results based on advanced filters
  const filteredResults = results.filter(item => {
    if (advancedFilters.dateFrom && item.date && item.date < advancedFilters.dateFrom) return false;
    if (advancedFilters.dateTo && item.date && item.date > advancedFilters.dateTo) return false;

    if (advancedFilters.tags.length > 0) {
      const itemTags = item.tags || [];
      if (!advancedFilters.tags.some(tag => itemTags.includes(tag))) return false;
    }

    if (advancedFilters.hasEvidence && item.type === 'document') {
      if (item.docType !== 'image' && item.docType !== 'audio') return false;
    }

    if (advancedFilters.hasFOIA) {
      const itemTags = item.tags || [];
      if (!itemTags.includes('FOIA')) return false;
    }

    return true;
  });

  // Sort results
  const sortedResults = [...filteredResults].sort((a, b) => {
    switch (sortBy) {
      case "date":
        return new Date(b.date || "").getTime() - new Date(a.date || "").getTime();
      case "title":
        return a.title.localeCompare(b.title);
      case "type":
        if (a.type !== b.type) return a.type.localeCompare(b.type);
        return a.title.localeCompare(b.title);
      default:
        return 0; // relevance - keep search order
    }
  });

  // Convert SearchableItems back to original types for components
  const documentResults = sortedResults
    .filter(item => item.type === 'document')
    .map(item => {
      const originalDoc = CASE_DOCUMENTS.find(doc => doc.id === item.id);
      return originalDoc!;
    })
    .filter(Boolean);

  const timelineResults = sortedResults
    .filter(item => item.type === 'timeline')
    .map(item => {
      const originalEvent = CASE_TIMELINE.find(event => event.id === item.id);
      return originalEvent!;
    })
    .filter(Boolean);

  const searchStats = {
    total: sortedResults.length,
    documents: documentResults.length,
    timeline: timelineResults.length,
  };

  return (
    <div className="flex-1 overflow-hidden">
      <Header
        title="Advanced Search"
        onSearch={handleSearch}
        onExport={handleExport}
      />

      <div className="p-6 overflow-y-auto h-full bg-muted/30 print-friendly">
        {/* Search Header */}
        <div className="flex items-center justify-between mb-6">
          <div>
            <h2 className="text-2xl font-bold mb-2">Advanced Case Search</h2>
            <p className="text-muted-foreground">
              Search across all documents, timeline events, and evidence with advanced filtering
            </p>
          </div>
          <div className="flex items-center gap-3">
            <Button
              variant="outline"
              onClick={() => setShowAdvanced(!showAdvanced)}
              data-testid="toggle-advanced-search"
            >
              <Filter className="w-4 h-4 mr-2" />
              Advanced Filters
            </Button>
            <Badge variant="outline" className="flex items-center gap-1">
              <Search className="w-3 h-3" />
              {searchStats.total} Results
            </Badge>
          </div>
        </div>

        {/* Search Bar */}
        <Card className="rounded-xl mb-6">
          <CardContent className="p-4">
            <div className="relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-muted-foreground w-5 h-5" />
              <Input
                type="search"
                placeholder="Search documents, timeline events, tags, summaries..."
                value={query}
                onChange={(e) => setQuery(e.target.value)}
                className="pl-12 pr-4 py-3 text-lg"
                data-testid="advanced-search-input"
              />
            </div>
          </CardContent>
        </Card>

        {/* Advanced Filters Panel */}
        {showAdvanced && (
          <Card className="rounded-xl mb-6">
            <CardHeader className="pb-4">
              <CardTitle className="text-lg font-semibold flex items-center gap-2">
                <Filter className="w-5 h-5" />
                Advanced Filters
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-6">
              {/* Date Range */}
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="dateFrom">Date From</Label>
                  <Input
                    id="dateFrom"
                    type="date"
                    value={advancedFilters.dateFrom}
                    onChange={(e) => setAdvancedFilters(prev => ({ ...prev, dateFrom: e.target.value }))}
                    data-testid="search-date-from"
                  />
                </div>
                <div>
                  <Label htmlFor="dateTo">Date To</Label>
                  <Input
                    id="dateTo"
                    type="date"
                    value={advancedFilters.dateTo}
                    onChange={(e) => setAdvancedFilters(prev => ({ ...prev, dateTo: e.target.value }))}
                    data-testid="search-date-to"
                  />
                </div>
              </div>

              {/* Content Type Filters */}
              <div className="space-y-3">
                <Label>Content Types</Label>
                <div className="flex flex-wrap gap-4">
                  <div className="flex items-center space-x-2">
                    <Checkbox
                      id="hasEvidence"
                      checked={advancedFilters.hasEvidence}
                      onCheckedChange={(checked) => 
                        setAdvancedFilters(prev => ({ ...prev, hasEvidence: !!checked }))
                      }
                    />
                    <Label htmlFor="hasEvidence" className="text-sm">Has Evidence</Label>
                  </div>
                  <div className="flex items-center space-x-2">
                    <Checkbox
                      id="hasFOIA"
                      checked={advancedFilters.hasFOIA}
                      onCheckedChange={(checked) => 
                        setAdvancedFilters(prev => ({ ...prev, hasFOIA: !!checked }))
                      }
                    />
                    <Label htmlFor="hasFOIA" className="text-sm">FOIA Related</Label>
                  </div>
                </div>
              </div>

              {/* Tags Filter */}
              {allTags.length > 0 && (
                <div className="space-y-3">
                  <Label>Tags</Label>
                  <div className="flex flex-wrap gap-2 max-h-32 overflow-y-auto">
                    {allTags.map(tag => (
                      <div key={tag} className="flex items-center space-x-2">
                        <Checkbox
                          id={`tag-${tag}`}
                          checked={advancedFilters.tags.includes(tag)}
                          onCheckedChange={(checked) => {
                            if (checked) {
                              setAdvancedFilters(prev => ({ 
                                ...prev, 
                                tags: [...prev.tags, tag] 
                              }));
                            } else {
                              setAdvancedFilters(prev => ({ 
                                ...prev, 
                                tags: prev.tags.filter(t => t !== tag) 
                              }));
                            }
                          }}
                        />
                        <Label htmlFor={`tag-${tag}`} className="text-sm">
                          <Badge variant="outline" className="text-xs">{tag}</Badge>
                        </Label>
                      </div>
                    ))}
                  </div>
                </div>
              )}

              <div className="flex items-center gap-2">
                <Button
                  variant="outline"
                  onClick={() => {
                    setAdvancedFilters({
                      dateFrom: "",
                      dateTo: "",
                      tags: [],
                      locations: [],
                      hasEvidence: false,
                      hasFOIA: false,
                    });
                    clearFilters();
                  }}
                  data-testid="clear-advanced-filters"
                >
                  Clear All Filters
                </Button>
              </div>
            </CardContent>
          </Card>
        )}

        {/* Active Filters */}
        {(activeFilters.length > 0 || query) && (
          <div className="flex flex-wrap items-center gap-2 mb-6">
            <span className="text-sm font-medium">Active filters:</span>
            {query && (
              <Badge variant="secondary" className="flex items-center gap-1">
                <Search className="w-3 h-3" />
                Search: "{query}"
                <Button
                  variant="ghost"
                  size="sm"
                  className="h-auto p-0 ml-1"
                  onClick={() => setQuery("")}
                >
                  <X className="w-3 h-3" />
                </Button>
              </Badge>
            )}
            {activeFilters.map(filter => (
              <Badge key={filter} variant="secondary" className="flex items-center gap-1">
                {filter}
                <Button
                  variant="ghost"
                  size="sm"
                  className="h-auto p-0 ml-1"
                  onClick={() => removeFilter(filter)}
                >
                  <X className="w-3 h-3" />
                </Button>
              </Badge>
            ))}
          </div>
        )}

        {/* Results Controls */}
        <div className="flex items-center justify-between mb-6">
          <div className="flex items-center gap-4">
            <div className="flex items-center gap-2">
              <Label>Sort by:</Label>
              <Select value={sortBy} onValueChange={setSortBy}>
                <SelectTrigger className="w-32" data-testid="search-sort-select">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="relevance">Relevance</SelectItem>
                  <SelectItem value="date">Date</SelectItem>
                  <SelectItem value="title">Title</SelectItem>
                  <SelectItem value="type">Type</SelectItem>
                </SelectContent>
              </Select>
            </div>
          </div>
          <div className="text-sm text-muted-foreground">
            {searchStats.total} results ({searchStats.documents} documents, {searchStats.timeline} timeline events)
          </div>
        </div>

        {/* Search Results */}
        {sortedResults.length > 0 ? (
          <Tabs defaultValue="all" className="space-y-6">
            <TabsList className="grid w-full grid-cols-3">
              <TabsTrigger value="all" className="flex items-center gap-2">
                All Results
                <Badge variant="secondary" className="text-xs">{searchStats.total}</Badge>
              </TabsTrigger>
              <TabsTrigger value="documents" className="flex items-center gap-2">
                Documents
                <Badge variant="secondary" className="text-xs">{searchStats.documents}</Badge>
              </TabsTrigger>
              <TabsTrigger value="timeline" className="flex items-center gap-2">
                Timeline
                <Badge variant="secondary" className="text-xs">{searchStats.timeline}</Badge>
              </TabsTrigger>
            </TabsList>

            <TabsContent value="all" className="space-y-6">
              {/* Documents */}
              {documentResults.length > 0 && (
                <div>
                  <h3 className="text-lg font-semibold mb-4 flex items-center gap-2">
                    <FileText className="w-5 h-5" />
                    Documents ({documentResults.length})
                  </h3>
                  <div className="grid sm:grid-cols-2 lg:grid-cols-3 gap-6 mb-8">
                    {documentResults.slice(0, 6).map((doc) => (
                      <DocumentCard
                        key={doc.id}
                        document={doc}
                        onOpen={setSelectedDocument}
                      />
                    ))}
                  </div>
                </div>
              )}

              {/* Timeline Events */}
              {timelineResults.length > 0 && (
                <div>
                  <h3 className="text-lg font-semibold mb-4 flex items-center gap-2">
                    <Clock className="w-5 h-5" />
                    Timeline Events ({timelineResults.length})
                  </h3>
                  <div className="space-y-6">
                    {timelineResults.slice(0, 5).map((event) => (
                      <TimelineEventComponent
                        key={event.id}
                        event={event}
                        onDocumentClick={handleDocumentClick}
                        showConnector={false}
                      />
                    ))}
                  </div>
                </div>
              )}
            </TabsContent>

            <TabsContent value="documents">
              <div className="grid sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
                {documentResults.map((doc) => (
                  <DocumentCard
                    key={doc.id}
                    document={doc}
                    onOpen={setSelectedDocument}
                  />
                ))}
              </div>
            </TabsContent>

            <TabsContent value="timeline">
              <div className="space-y-6">
                {timelineResults.map((event) => (
                  <TimelineEventComponent
                    key={event.id}
                    event={event}
                    onDocumentClick={handleDocumentClick}
                    showConnector={false}
                  />
                ))}
              </div>
            </TabsContent>
          </Tabs>
        ) : (
          <Card className="rounded-xl">
            <CardContent className="p-12 text-center">
              <Search className="w-16 h-16 mx-auto mb-4 text-muted-foreground/50" />
              <h3 className="text-lg font-medium mb-2">No results found</h3>
              <p className="text-muted-foreground mb-4">
                {query 
                  ? `No items match your search for "${query}"`
                  : "Enter a search term to find documents and timeline events"
                }
              </p>
              <div className="flex flex-wrap justify-center gap-2">
                <Button variant="outline" size="sm" onClick={() => setQuery("FOIA")}>
                  Search FOIA
                </Button>
                <Button variant="outline" size="sm" onClick={() => setQuery("courthouse")}>
                  Search Courthouse
                </Button>
                <Button variant="outline" size="sm" onClick={() => setQuery("First Amendment")}>
                  Search First Amendment
                </Button>
              </div>
            </CardContent>
          </Card>
        )}

        {/* Document Viewer Modal */}
        <Dialog open={!!selectedDocument} onOpenChange={() => setSelectedDocument(null)}>
          <DialogContent className="max-w-4xl">
            <DialogHeader>
              <DialogTitle className="flex items-center gap-2">
                <FileText className="w-5 h-5" />
                Document Viewer
              </DialogTitle>
            </DialogHeader>
            {selectedDocument && (
              <div className="space-y-4">
                <div className="flex items-center gap-2 text-sm text-muted-foreground">
                  {selectedDocument.date && <span>{formatDate(selectedDocument.date)}</span>}
                  {selectedDocument.sourceNote && (
                    <>
                      <span>•</span>
                      <span>{selectedDocument.sourceNote}</span>
                    </>
                  )}
                </div>
                {selectedDocument.path ? (
                  selectedDocument.type === "image" ? (
                    <img
                      src={selectedDocument.path}
                      alt={selectedDocument.title}
                      className="w-full rounded-lg border max-h-[70vh] object-contain"
                    />
                  ) : (
                    <iframe
                      title={selectedDocument.title}
                      src={selectedDocument.path}
                      className="w-full h-[70vh] rounded-lg border"
                    />
                  )
                ) : (
                  <div className="text-center py-8 text-muted-foreground">
                    No preview available for this document
                  </div>
                )}
                <div className="flex justify-between items-center">
                  <div>
                    <h3 className="font-semibold">{selectedDocument.title}</h3>
                    <p className="text-sm text-muted-foreground">{selectedDocument.summary}</p>
                  </div>
                  {selectedDocument.path && (
                    <Button variant="outline" size="sm" asChild>
                      <a
                        href={selectedDocument.path}
                        target="_blank"
                        rel="noopener noreferrer"
                        data-testid="open-document-source"
                      >
                        Open Source
                      </a>
                    </Button>
                  )}
                </div>
              </div>
            )}
          </DialogContent>
        </Dialog>
      </div>
    </div>
  );
}

client/src/pages/timeline.tsx.content:
import { useState } from "react";
import { Header } from "@/components/layout/header";
import { TimelineEventComponent } from "@/components/case/timeline-event";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Calendar, Filter, FileText, BarChart } from "lucide-react";
import { ResponsiveContainer, BarChart as RechartsBarChart, Bar, XAxis, YAxis, Tooltip } from "recharts";
import { CASE_TIMELINE, Doc, formatDate, getDocumentById } from "@/lib/case-data";

export default function Timeline() {
  const [selectedDocument, setSelectedDocument] = useState<Doc | null>(null);
  const [filterType, setFilterType] = useState<string>("all");
  const [dateRange, setDateRange] = useState({ start: "", end: "" });

  const handleSearch = (query: string) => {
    console.log("Searching timeline:", query);
  };

  const handleExport = () => {
    window.print();
  };

  const handleDocumentClick = (docId: string) => {
    const doc = getDocumentById(docId);
    if (doc) {
      setSelectedDocument(doc);
    }
  };

  // Filter timeline events
  const filteredTimeline = CASE_TIMELINE.filter(event => {
    if (filterType !== "all") {
      const hasTag = event.tags?.includes(filterType);
      if (!hasTag) return false;
    }

    if (dateRange.start && event.date < dateRange.start) return false;
    if (dateRange.end && event.date > dateRange.end) return false;

    return true;
  });

  // Create chart data
  const chartData = CASE_TIMELINE.reduce((acc, event) => {
    const month = new Date(event.date).toISOString().slice(0, 7);
    const existing = acc.find(item => item.month === month);
    if (existing) {
      existing.events += 1;
    } else {
      acc.push({ month, events: 1 });
    }
    return acc;
  }, [] as { month: string; events: number }[])
  .sort((a, b) => a.month.localeCompare(b.month));

  const filterOptions = [
    { value: "all", label: "All Events" },
    { value: "courthouse", label: "Courthouse Events" },
    { value: "FOIA", label: "FOIA Related" },
    { value: "critical", label: "Critical Events" },
    { value: "recording", label: "Recordings" },
  ];

  return (
    <div className="flex-1 overflow-hidden">
      <Header
        title="Case Timeline"
        onSearch={handleSearch}
        onExport={handleExport}
      />

      <div className="p-4 sm:p-6 overflow-y-auto h-full bg-muted/30 print-friendly">
        {/* Timeline Header */}
        <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between gap-4 mb-6">
          <div>
            <h2 className="text-xl sm:text-2xl font-bold mb-2">Case Timeline & Events</h2>
            <p className="text-sm sm:text-base text-muted-foreground">
              Chronological view of all case developments and key events
            </p>
          </div>
          <div className="flex items-center gap-3">
            <Badge variant="outline" className="flex items-center gap-1">
              <Calendar className="w-3 h-3" />
              {filteredTimeline.length} Events
            </Badge>
          </div>
        </div>

        {/* Filters and Chart */}
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-4 sm:gap-6 mb-6 sm:mb-8">
          {/* Filters */}
          <Card className="lg:col-span-1 rounded-xl">
            <CardHeader className="pb-4">
              <CardTitle className="text-lg font-semibold flex items-center gap-2">
                <Filter className="w-5 h-5" />
                Filter Timeline
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-4 p-3 sm:p-4">
              <div>
                <label className="text-sm font-medium mb-2 block">Event Type</label>
                <Select value={filterType} onValueChange={setFilterType}>
                  <SelectTrigger data-testid="timeline-filter-select">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    {filterOptions.map(option => (
                      <SelectItem key={option.value} value={option.value}>
                        {option.label}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>

              <div>
                <label className="text-sm font-medium mb-2 block">Date Range</label>
                <div className="grid grid-cols-1 sm:grid-cols-2 gap-2">
                  <Input
                    type="date"
                    placeholder="Start date"
                    value={dateRange.start}
                    onChange={(e) => setDateRange(prev => ({ ...prev, start: e.target.value }))}
                    data-testid="timeline-start-date"
                  />
                  <Input
                    type="date"
                    placeholder="End date"
                    value={dateRange.end}
                    onChange={(e) => setDateRange(prev => ({ ...prev, end: e.target.value }))}
                    data-testid="timeline-end-date"
                  />
                </div>
              </div>

              <Button
                variant="outline"
                className="w-full"
                onClick={() => {
                  setFilterType("all");
                  setDateRange({ start: "", end: "" });
                }}
                data-testid="clear-timeline-filters"
              >
                Clear Filters
              </Button>
            </CardContent>
          </Card>

          {/* Events Chart */}
          <Card className="lg:col-span-2 rounded-xl">
            <CardHeader className="pb-4">
              <CardTitle className="text-lg font-semibold flex items-center gap-2">
                <BarChart className="w-5 h-5" />
                Events by Month
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="h-48">
                <ResponsiveContainer width="100%" height="100%">
                  <RechartsBarChart data={chartData}>
                    <XAxis 
                      dataKey="month" 
                      fontSize={12} 
                      tickLine={false} 
                      axisLine={false}
                      tickFormatter={(value) => new Date(value + "-01").toLocaleDateString('en-US', { month: 'short', year: '2-digit' })}
                    />
                    <YAxis 
                      allowDecimals={false} 
                      fontSize={12} 
                      tickLine={false} 
                      axisLine={false} 
                    />
                    <Tooltip 
                      cursor={{ fill: "hsl(var(--muted))" }}
                      labelFormatter={(value) => new Date(value + "-01").toLocaleDateString('en-US', { month: 'long', year: 'numeric' })}
                      formatter={(value) => [value, "Events"]}
                    />
                    <Bar 
                      dataKey="events" 
                      radius={[6, 6, 0, 0]}
                      fill="hsl(var(--primary))"
                    />
                  </RechartsBarChart>
                </ResponsiveContainer>
              </div>
            </CardContent>
          </Card>
        </div>

        {/* Timeline Events */}
        <Card className="rounded-xl">
          <CardHeader className="pb-4">
            <CardTitle className="text-lg font-semibold">Timeline Events</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="relative">
              <div className="absolute left-6 top-0 bottom-0 w-0.5 timeline-line"></div>
              <div className="space-y-8">
                {filteredTimeline.map((event) => (
                  <TimelineEventComponent
                    key={event.id}
                    event={event}
                    onDocumentClick={handleDocumentClick}
                  />
                ))}
                {filteredTimeline.length === 0 && (
                  <div className="text-center py-8 text-muted-foreground">
                    No events match the current filters
                  </div>
                )}
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Document Viewer Modal */}
        <Dialog open={!!selectedDocument} onOpenChange={() => setSelectedDocument(null)}>
          <DialogContent className="max-w-4xl">
            <DialogHeader>
              <DialogTitle className="flex items-center gap-2">
                <FileText className="w-5 h-5" />
                Document Viewer
              </DialogTitle>
            </DialogHeader>
            {selectedDocument && (
              <div className="space-y-4">
                <div className="flex items-center gap-2 text-sm text-muted-foreground">
                  {selectedDocument.date && <span>{formatDate(selectedDocument.date)}</span>}
                  {selectedDocument.sourceNote && (
                    <>
                      <span>•</span>
                      <span>{selectedDocument.sourceNote}</span>
                    </>
                  )}
                </div>
                {selectedDocument.path ? (
                  selectedDocument.type === "image" ? (
                    <img
                      src={selectedDocument.path}
                      alt={selectedDocument.title}
                      className="w-full rounded-lg border max-h-[70vh] object-contain"
                    />
                  ) : (
                    <iframe
                      title={selectedDocument.title}
                      src={selectedDocument.path}
                      className="w-full h-[70vh] rounded-lg border"
                    />
                  )
                ) : (
                  <div className="text-center py-8 text-muted-foreground">
                    No preview available for this document
                  </div>
                )}
                <div className="flex justify-between items-center">
                  <div>
                    <h3 className="font-semibold">{selectedDocument.title}</h3>
                    <p className="text-sm text-muted-foreground">{selectedDocument.summary}</p>
                  </div>
                  {selectedDocument.path && (
                    <Button variant="outline" size="sm" asChild>
                      <a
                        href={selectedDocument.path}
                        target="_blank"
                        rel="noopener noreferrer"
                        data-testid="open-document-source"
                      >
                        Open Source
                      </a>
                    </Button>
                  )}
                </div>
              </div>
            )}
          </DialogContent>
        </Dialog>
      </div>
    </div>
  );
}

client/src/components/case/document-card.content:
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { FileText, Image, Video, List, Info, ExternalLink } from "lucide-react";
import { Doc, DocType, formatDate } from "@/lib/case-data";

interface DocumentCardProps {
  document: Doc;
  onOpen?: (doc: Doc) => void;
}

export function DocumentCard({ document, onOpen }: DocumentCardProps) {
  const getTypeIcon = (type: DocType) => {
    switch (type) {
      case "pdf":
      case "letter":
        return <FileText className="h-4 w-4" />;
      case "image":
        return <Image className="h-4 w-4" />;
      case "audio":
        return <Video className="h-4 w-4" />;
      case "transcript":
        return <List className="h-4 w-4" />;
      default:
        return <Info className="h-4 w-4" />;
    }
  };

  const getTypeColor = (type: DocType) => {
    switch (type) {
      case "pdf":
      case "letter":
        return "chart-4";
      case "image":
        return "chart-3";
      case "audio":
        return "chart-2";
      case "transcript":
        return "primary";
      default:
        return "muted-foreground";
    }
  };

  return (
    <Card className="rounded-xl sm:rounded-2xl hover:shadow-md transition-all duration-200 evidence-zoom">
      <CardHeader className="pb-2 p-3 sm:p-4">
        <div className="flex items-start justify-between gap-2 sm:gap-3">
          <div className="flex-1 min-w-0">
            <CardTitle className="text-sm sm:text-base leading-tight line-clamp-2">
              {document.title}
            </CardTitle>
            {document.date && (
              <div className="text-xs text-muted-foreground mt-1">
                {formatDate(document.date)}
              </div>
            )}
          </div>
          <div className={`text-${getTypeColor(document.type)} flex-shrink-0`}>
            {getTypeIcon(document.type)}
          </div>
        </div>
      </CardHeader>

      <CardContent className="space-y-3 p-3 sm:p-4 pt-0">
        <p className="text-sm line-clamp-2 sm:line-clamp-3 min-h-[2.5rem] sm:min-h-[3.5rem] text-muted-foreground">
          {document.summary}
        </p>

        {document.tags && document.tags.length > 0 && (
          <div className="flex flex-wrap gap-1 sm:gap-2">
            {document.tags.map((tag) => (
              <Badge key={tag} variant="secondary" className="text-xs">
                {tag}
              </Badge>
            ))}
          </div>
        )}

        <div className="flex flex-col sm:flex-row gap-2 pt-1">
          <Button
            variant="outline"
            size="sm"
            className="flex-1 text-xs sm:text-sm"
            onClick={() => onOpen?.(document)}
            data-testid={`open-document-${document.id}`}
          >
            Open
          </Button>

          {document.path && (
            <Button
              variant="ghost"
              size="sm"
              asChild
              data-testid={`view-source-${document.id}`}
            >
              <a
                href={document.path}
                target="_blank"
                rel="noopener noreferrer"
                className="flex items-center gap-1"
              >
                <ExternalLink className="h-3 w-3" />
                Source
              </a>
            </Button>
          )}
        </div>

        {document.sourceNote && (
          <div className="text-xs text-muted-foreground italic border-t pt-2">
            {document.sourceNote}
          </div>
        )}
      </CardContent>
    </Card>
  );
}

client/src/components/case/evidence-gall.content:
import { useState } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Badge } from "@/components/ui/badge";
import { ZoomIn, Download, Play, Pause } from "lucide-react";
import { CASE_DOCUMENTS, Doc, formatDate } from "@/lib/case-data";

interface EvidenceGalleryProps {
  maxItems?: number;
  showViewAll?: boolean;
}

export function EvidenceGallery({ maxItems = 4, showViewAll = true }: EvidenceGalleryProps) {
  const [selectedEvidence, setSelectedEvidence] = useState<Doc | null>(null);
  const [isPlaying, setIsPlaying] = useState(false);

  const evidenceItems = CASE_DOCUMENTS.filter(doc => 
    doc.type === "image" || doc.type === "audio"
  ).slice(0, maxItems);

  const renderEvidencePreview = (item: Doc) => {
    if (item.type === "image") {
      return (
        <img
          src={item.path || "https://images.unsplash.com/photo-1589578527966-fdac0f44566c?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&h=300"}
          alt={item.title}
          className="w-full h-32 object-cover"
        />
      );
    }

    if (item.type === "audio") {
      return (
        <div className="w-full h-32 bg-gradient-to-br from-primary/20 to-chart-2/20 flex items-center justify-center">
          <div className="text-center">
            <Play className="w-8 h-8 text-primary mx-auto mb-2" />
            <span className="text-sm font-medium">Audio Evidence</span>
          </div>
        </div>
      );
    }

    return (
      <div className="w-full h-32 bg-muted flex items-center justify-center">
        <span className="text-muted-foreground">Preview not available</span>
      </div>
    );
  };

  const renderEvidenceModal = (item: Doc) => {
    if (item.type === "image") {
      return (
        <div className="space-y-4">
          <img
            src={item.path || "https://images.unsplash.com/photo-1589578527966-fdac0f44566c?ixlib=rb-4.0.3&auto=format&fit=crop&w=1200&h=800"}
            alt={item.title}
            className="w-full rounded-lg border border-border max-h-[70vh] object-contain"
          />
          <div className="flex justify-between items-center">
            <div className="space-y-1">
              <p className="text-sm font-medium">{item.title}</p>
              {item.date && (
                <p className="text-xs text-muted-foreground">{formatDate(item.date)}</p>
              )}
            </div>
            <Button size="sm" variant="outline" data-testid="download-evidence">
              <Download className="w-4 h-4 mr-2" />
              Download
            </Button>
          </div>
        </div>
      );
    }

    if (item.type === "audio") {
      return (
        <div className="space-y-4">
          <div className="bg-gradient-to-br from-primary/10 to-chart-2/10 rounded-lg p-8 text-center">
            <div className="w-24 h-24 bg-primary/20 rounded-full flex items-center justify-center mx-auto mb-4">
              {isPlaying ? (
                <Pause className="w-12 h-12 text-primary" />
              ) : (
                <Play className="w-12 h-12 text-primary" />
              )}
            </div>
            <h3 className="text-lg font-semibold mb-2">{item.title}</h3>
            {item.date && (
              <p className="text-sm text-muted-foreground mb-4">{formatDate(item.date)}</p>
            )}
            <Button
              onClick={() => setIsPlaying(!isPlaying)}
              className="mb-4"
              data-testid="play-audio-evidence"
            >
              {isPlaying ? "Pause" : "Play"} Audio
            </Button>
            <div className="w-full bg-muted rounded-full h-2">
              <div className="bg-primary h-2 rounded-full" style={{ width: "30%" }}></div>
            </div>
          </div>
          <div className="flex justify-between items-center">
            <p className="text-sm text-muted-foreground">{item.summary}</p>
            <Button size="sm" variant="outline" data-testid="download-audio">
              <Download className="w-4 h-4 mr-2" />
              Download
            </Button>
          </div>
        </div>
      );
    }

    return <div>Preview not available for this evidence type.</div>;
  };

  return (
    <>
      <Card className="rounded-xl">
        <CardHeader className="pb-4">
          <div className="flex items-center justify-between">
            <CardTitle className="text-lg font-semibold">Key Evidence</CardTitle>
            {showViewAll && (
              <Button variant="ghost" size="sm" data-testid="view-all-evidence">
                View All
              </Button>
            )}
          </div>
        </CardHeader>

        <CardContent className="space-y-4">
          <div className="grid grid-cols-2 gap-4">
            {evidenceItems.map((item) => (
              <div
                key={item.id}
                className="evidence-zoom cursor-pointer rounded-lg overflow-hidden border border-border"
                onClick={() => setSelectedEvidence(item)}
                data-testid={`evidence-item-${item.id}`}
              >
                {renderEvidencePreview(item)}
                <div className="p-3">
                  <h4 className="font-medium text-sm mb-1 line-clamp-1">{item.title}</h4>
                  <p className="text-xs text-muted-foreground line-clamp-1">
                    {item.summary}
                  </p>
                  {item.tags && (
                    <div className="flex flex-wrap gap-1 mt-2">
                      {item.tags.slice(0, 2).map((tag) => (
                        <Badge key={tag} variant="secondary" className="text-xs">
                          {tag}
                        </Badge>
                      ))}
                    </div>
                  )}
                </div>
              </div>
            ))}
          </div>

          <div className="bg-muted/50 rounded-lg p-4">
            <div className="flex items-center gap-2 mb-2">
              <ZoomIn className="text-primary w-4 h-4" />
              <span className="font-medium text-sm">Evidence Summary</span>
            </div>
            <p className="text-xs text-muted-foreground">
              All evidence items are categorized, timestamped, and cross-referenced with timeline events. 
              Digital chain of custody maintained.
            </p>
          </div>
        </CardContent>
      </Card>

      <Dialog open={!!selectedEvidence} onOpenChange={() => setSelectedEvidence(null)}>
        <DialogContent className="max-w-4xl">
          <DialogHeader>
            <DialogTitle className="flex items-center gap-2">
              <ZoomIn className="w-5 h-5" />
              Evidence Viewer
            </DialogTitle>
          </DialogHeader>
          {selectedEvidence && renderEvidenceModal(selectedEvidence)}
        </DialogContent>
      </Dialog>
    </>
  );
}

