import { useState, useEffect } from "react";
import { loadStripe } from "@stripe/stripe-js";
import { Elements, PaymentElement, useStripe, useElements } from "@stripe/react-stripe-js";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Separator } from "@/components/ui/separator";
import { CheckCircle, Crown, Clock, CreditCard, Shield, Zap, AlertTriangle } from "lucide-react";
import { useAuth } from "@/lib/auth-context";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { CouponInput } from "@/components/coupon-input";

const stripePromise = loadStripe(import.meta.env.VITE_STRIPE_PUBLIC_KEY!);

interface SubscriptionStatus {
  status: string;
  trialEndsAt?: string;
  subscriptionEndsAt?: string;
  isTrialActive: boolean;
  isSubscriptionActive: boolean;
}

function SubscriptionForm({ clientSecret, onSuccess, appliedCoupon }: { 
  clientSecret: string; 
  onSuccess: () => void; 
  appliedCoupon?: CouponValidationResult | null;
}) {
  const stripe = useStripe();
  const elements = useElements();
  const [isLoading, setIsLoading] = useState(false);
  const { toast } = useToast();

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!stripe || !elements) {
      return;
    }

    setIsLoading(true);

    const { error } = await stripe.confirmPayment({
      elements,
      confirmParams: {
        return_url: window.location.origin,
      },
    });

    if (error) {
      toast({
        title: "Payment Failed",
        description: error.message,
        variant: "destructive",
      });
    } else {
      // Apply coupon if valid
      if (appliedCoupon?.valid && appliedCoupon.coupon) {
        try {
          await apiRequest('/api/coupons/apply', {
            method: 'POST',
            data: {
              couponId: appliedCoupon.coupon.id,
              originalAmount: 14.95,
              discountApplied: appliedCoupon.discountAmount || 0,
              subscriptionId: clientSecret, // Use client secret as temp ID
            }
          });
        } catch (error) {
          console.error('Failed to record coupon usage:', error);
        }
      }

      toast({
        title: "Payment Successful",
        description: appliedCoupon?.valid ? 
          `Welcome to CaseBuddy Pro! Your coupon saved you $${appliedCoupon.discountAmount?.toFixed(2)}` :
          "Welcome to CaseBuddy Pro!",
      });
      onSuccess();
    }

    setIsLoading(false);
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-6">
      <PaymentElement />
      <Button 
        type="submit" 
        disabled={!stripe || isLoading} 
        className="w-full"
        data-testid="button-confirm-payment"
      >
        {isLoading ? "Processing..." : "Confirm Payment"}
      </Button>
    </form>
  );
}

interface CouponValidationResult {
  valid: boolean;
  coupon?: {
    id: number;
    code: string;
    description: string | null;
    discountType: 'percentage' | 'fixed';
    discountValue: string;
  };
  error?: string;
  discountAmount?: number;
  finalAmount?: number;
}

export default function SubscriptionPage() {
  const [subscriptionStatus, setSubscriptionStatus] = useState<SubscriptionStatus | null>(null);
  const [clientSecret, setClientSecret] = useState<string | null>(null);
  const [isLoading, setIsLoading] = useState(true);
  const [appliedCoupon, setAppliedCoupon] = useState<CouponValidationResult | null>(null);
  const { user } = useAuth();
  const { toast } = useToast();

  const MONTHLY_PRICE = 14.95;
  const finalPrice = appliedCoupon?.valid ? appliedCoupon.finalAmount || MONTHLY_PRICE : MONTHLY_PRICE;

  useEffect(() => {
    loadSubscriptionStatus();
  }, []);

  const loadSubscriptionStatus = async () => {
    try {
      const response = await apiRequest("/api/subscription/status");
      setSubscriptionStatus(response);
    } catch (error) {
      console.error("Failed to load subscription status:", error);
    } finally {
      setIsLoading(false);
    }
  };

  const handleStartSubscription = async () => {
    try {
      setIsLoading(true);
      const response = await apiRequest("/api/subscription/start", {
        method: "POST",
        data: {
          couponCode: appliedCoupon?.valid ? appliedCoupon.coupon?.code : undefined,
          finalAmount: finalPrice,
        }
      });

      if (response.clientSecret) {
        setClientSecret(response.clientSecret);
      } else {
        // Trial started successfully
        toast({
          title: "Trial Started!",
          description: "Your 2-week free trial has begun. Enjoy CaseBuddy Pro!",
        });
        loadSubscriptionStatus();
      }
    } catch (error: any) {
      toast({
        title: "Error",
        description: error.message || "Failed to start subscription",
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };

  const handleCancelSubscription = async () => {
    if (!confirm("Are you sure you want to cancel your subscription? You'll lose access to pro features at the end of your billing period.")) {
      return;
    }

    try {
      await apiRequest("/api/subscription/cancel", {
        method: "POST",
      });

      toast({
        title: "Subscription Canceled",
        description: "Your subscription has been canceled. You'll retain access until the end of your billing period.",
      });

      loadSubscriptionStatus();
    } catch (error: any) {
      toast({
        title: "Error",
        description: error.message || "Failed to cancel subscription",
        variant: "destructive",
      });
    }
  };

  const handleManageBilling = async () => {
    try {
      const response = await apiRequest("/api/subscription/portal", {
        method: "POST",
      });

      window.open(response.url, "_blank");
    } catch (error: any) {
      toast({
        title: "Error",
        description: error.message || "Failed to open billing portal",
        variant: "destructive",
      });
    }
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString("en-US", {
      year: "numeric",
      month: "long",
      day: "numeric",
    });
  };

  const getDaysRemaining = (dateString: string) => {
    const date = new Date(dateString);
    const now = new Date();
    const diffTime = date.getTime() - now.getTime();
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
    return Math.max(0, diffDays);
  };

  if (isLoading) {
    return (
      <div className="flex h-screen items-center justify-center">
        <div className="text-center">
          <div className="w-8 h-8 border-4 border-primary border-t-transparent rounded-full animate-spin mx-auto mb-4"></div>
          <p className="text-muted-foreground">Loading subscription details...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="container mx-auto p-6 max-w-4xl">
      <div className="mb-8 text-center">
        <h1 className="text-3xl font-bold mb-2">CaseBuddy Pro</h1>
        <p className="text-muted-foreground">Your Legal Case Assistant - Professional Plan</p>
      </div>

      <div className="grid md:grid-cols-2 gap-6">
        {/* Current Status */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Crown className="w-5 h-5" />
              Current Plan
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            {subscriptionStatus?.isTrialActive && (
              <Alert className="border-blue-200 bg-blue-50">
                <Clock className="w-4 h-4" />
                <AlertDescription>
                  <div className="flex items-center justify-between">
                    <span>Free Trial Active</span>
                    <Badge variant="outline" className="bg-blue-100 text-blue-800">
                      {subscriptionStatus.trialEndsAt && 
                        `${getDaysRemaining(subscriptionStatus.trialEndsAt)} days left`
                      }
                    </Badge>
                  </div>
                  {subscriptionStatus.trialEndsAt && (
                    <p className="text-sm mt-1">
                      Trial ends {formatDate(subscriptionStatus.trialEndsAt)}
                    </p>
                  )}
                </AlertDescription>
              </Alert>
            )}

            {subscriptionStatus?.status === "active" && (
              <Alert className="border-green-200 bg-green-50">
                <CheckCircle className="w-4 h-4" />
                <AlertDescription>
                  <div className="flex items-center justify-between">
                    <span>Pro Plan Active</span>
                    <Badge className="bg-green-100 text-green-800">
                      $14.95/month
                    </Badge>
                  </div>
                  {subscriptionStatus.subscriptionEndsAt && (
                    <p className="text-sm mt-1">
                      Next billing: {formatDate(subscriptionStatus.subscriptionEndsAt)}
                    </p>
                  )}
                </AlertDescription>
              </Alert>
            )}

            {subscriptionStatus?.status === "canceled" && (
              <Alert className="border-yellow-200 bg-yellow-50">
                <AlertTriangle className="w-4 h-4" />
                <AlertDescription>
                  <span>Subscription Canceled</span>
                  {subscriptionStatus.subscriptionEndsAt && (
                    <p className="text-sm mt-1">
                      Access until {formatDate(subscriptionStatus.subscriptionEndsAt)}
                    </p>
                  )}
                </AlertDescription>
              </Alert>
            )}

            <div className="space-y-3">
              <div className="flex items-center justify-between">
                <span className="text-sm">User</span>
                <span className="font-medium">{user?.email}</span>
              </div>
              <div className="flex items-center justify-between">
                <span className="text-sm">Status</span>
                <Badge variant={subscriptionStatus?.isSubscriptionActive ? "default" : "secondary"}>
                  {subscriptionStatus?.isSubscriptionActive ? "Active" : "Inactive"}
                </Badge>
              </div>
            </div>

            <Separator />

            <div className="space-y-2">
              {subscriptionStatus?.status === "trial" && (
                <div className="space-y-4">
                  <CouponInput
                    orderAmount={MONTHLY_PRICE}
                    planType="pro"
                    onCouponApplied={(coupon) => setAppliedCoupon(coupon)}
                    onCouponRemoved={() => setAppliedCoupon(null)}
                  />
                  <Button 
                    onClick={handleStartSubscription} 
                    className="w-full"
                    data-testid="button-start-subscription"
                  >
                    Upgrade to Pro (${finalPrice.toFixed(2)}/month)
                  </Button>
                </div>
              )}

              {subscriptionStatus?.status === "active" && (
                <>
                  <Button 
                    onClick={handleManageBilling} 
                    variant="outline" 
                    className="w-full"
                    data-testid="button-manage-billing"
                  >
                    <CreditCard className="w-4 h-4 mr-2" />
                    Manage Billing
                  </Button>
                  <Button 
                    onClick={handleCancelSubscription} 
                    variant="outline" 
                    className="w-full text-red-600 hover:text-red-700"
                    data-testid="button-cancel-subscription"
                  >
                    Cancel Subscription
                  </Button>
                </>
              )}
            </div>
          </CardContent>
        </Card>

        {/* Features */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Zap className="w-5 h-5" />
              Pro Features
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-3">
              <div className="flex items-start gap-3">
                <CheckCircle className="w-5 h-5 text-green-600 mt-0.5" />
                <div>
                  <p className="font-medium">AI-Powered Legal Analytics</p>
                  <p className="text-sm text-muted-foreground">
                    Case outcome prediction and judge behavior analysis
                  </p>
                </div>
              </div>

              <div className="flex items-start gap-3">
                <CheckCircle className="w-5 h-5 text-green-600 mt-0.5" />
                <div>
                  <p className="font-medium">Automated Brief Generation</p>
                  <p className="text-sm text-muted-foreground">
                    Professional legal briefs with customizable templates
                  </p>
                </div>
              </div>

              <div className="flex items-start gap-3">
                <CheckCircle className="w-5 h-5 text-green-600 mt-0.5" />
                <div>
                  <p className="font-medium">Advanced Case Management</p>
                  <p className="text-sm text-muted-foreground">
                    Unlimited cases, documents, and timeline events
                  </p>
                </div>
              </div>

              <div className="flex items-start gap-3">
                <CheckCircle className="w-5 h-5 text-green-600 mt-0.5" />
                <div>
                  <p className="font-medium">Priority Support</p>
                  <p className="text-sm text-muted-foreground">
                    Get help when you need it most
                  </p>
                </div>
              </div>

              <div className="flex items-start gap-3">
                <Shield className="w-5 h-5 text-green-600 mt-0.5" />
                <div>
                  <p className="font-medium">Secure & Compliant</p>
                  <p className="text-sm text-muted-foreground">
                    Bank-level security for your sensitive legal data
                  </p>
                </div>
              </div>
            </div>

            <Separator className="my-4" />

            <div className="text-center space-y-2">
              {appliedCoupon?.valid && appliedCoupon.discountAmount && (
                <div className="space-y-1">
                  <p className="text-lg text-gray-500 line-through">${MONTHLY_PRICE.toFixed(2)}</p>
                  <p className="text-sm text-green-600">Save ${appliedCoupon.discountAmount.toFixed(2)}</p>
                </div>
              )}
              <p className="text-2xl font-bold">
                ${finalPrice.toFixed(2)}
                <span className="text-sm font-normal text-muted-foreground">/month</span>
              </p>
              <p className="text-sm text-muted-foreground">14-day free trial • Cancel anytime</p>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Payment Form */}
      {clientSecret && (
        <Card className="mt-6">
          <CardHeader>
            <CardTitle>Complete Your Subscription</CardTitle>
          </CardHeader>
          <CardContent>
            <Elements stripe={stripePromise} options={{ clientSecret }}>
              <SubscriptionForm 
                clientSecret={clientSecret} 
                appliedCoupon={appliedCoupon}
                onSuccess={() => {
                  setClientSecret(null);
                  setAppliedCoupon(null);
                  loadSubscriptionStatus();
                }} 
              />
            </Elements>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

client/src/pages/video-evidence.tsx.content:
import { useState } from "react";
import { Header } from "@/components/layout/header";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Play, Download, Upload, Grid, List, Camera, Video as VideoIcon } from "lucide-react";
import { ObjectUploader } from "@/components/ObjectUploader";
import { CASE_DOCUMENTS, Doc } from "@/lib/case-data";
import type { UploadResult } from "@uppy/core";

export default function VideoEvidence() {
  const [selectedVideo, setSelectedVideo] = useState<Doc | null>(null);
  const [viewMode, setViewMode] = useState<"grid" | "list">("grid");
  const [filterQuality, setFilterQuality] = useState<string>("all");
  const [searchQuery, setSearchQuery] = useState("");

  const handleSearch = (query: string) => {
    setSearchQuery(query);
  };

  const handleExport = () => {
    window.print();
  };

  const handleUploadComplete = (result: UploadResult<Record<string, unknown>, Record<string, unknown>>) => {
    console.log("Video upload completed:", result);
    if (result.successful && result.successful.length > 0) {
      console.log('Video files uploaded successfully:', result.successful.map(file => file.name));
    }
  };

  const getUploadParameters = async () => {
    try {
      const response = await fetch('/api/objects/upload', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
      });
      const data = await response.json();
      return { method: 'PUT' as const, url: data.uploadURL };
    } catch (error) {
      console.error('Failed to get upload URL:', error);
      throw error;
    }
  };

  // Filter video evidence items
  const videoItems = CASE_DOCUMENTS.filter(doc => doc.type === "video");

  const filteredVideos = videoItems.filter(item => {
    if (searchQuery.trim()) {
      const searchLower = searchQuery.toLowerCase();
      return (
        item.title.toLowerCase().includes(searchLower) ||
        item.summary?.toLowerCase().includes(searchLower) ||
        item.tags?.some(tag => tag.toLowerCase().includes(searchLower))
      );
    }
    return true;
  });

  const videoStats = {
    total: videoItems.length,
    hours: Math.floor(videoItems.length * 1.5), // Estimated total hours
    evidence: videoItems.filter(item => item.tags?.includes("evidence")).length,
    surveillance: videoItems.filter(item => item.tags?.includes("surveillance")).length,
  };

  const renderVideoPreview = (item: Doc) => (
    <div className="w-full h-48 bg-gradient-to-br from-chart-4/20 to-chart-1/20 flex items-center justify-center rounded-lg border">
      <div className="text-center">
        <Play className="w-16 h-16 text-chart-4 mx-auto mb-3" />
        <span className="text-sm font-medium text-chart-4">Video Evidence</span>
        <p className="text-xs text-muted-foreground mt-1">Click to play</p>
      </div>
    </div>
  );

  const renderListView = (item: Doc) => (
    <Card key={item.id} className="rounded-xl mb-4">
      <CardContent className="p-4">
        <div className="flex items-start gap-4">
          <div className="w-24 h-16 bg-chart-4/20 rounded-lg flex items-center justify-center flex-shrink-0">
            <Play className="w-6 h-6 text-chart-4" />
          </div>
          <div className="flex-1 min-w-0">
            <h3 className="font-semibold text-sm mb-1 line-clamp-1">{item.title}</h3>
            <p className="text-xs text-muted-foreground mb-2 line-clamp-2">{item.summary}</p>
            <div className="flex items-center gap-2 mb-2">
              <Badge variant="outline" className="text-xs">
                {item.type.toUpperCase()}
              </Badge>
              <span className="text-xs text-muted-foreground">{item.date}</span>
            </div>
            {item.tags && (
              <div className="flex flex-wrap gap-1">
                {item.tags.slice(0, 3).map((tag) => (
                  <Badge key={tag} variant="secondary" className="text-xs">
                    {tag}
                  </Badge>
                ))}
                {item.tags.length > 3 && (
                  <Badge variant="secondary" className="text-xs">
                    +{item.tags.length - 3}
                  </Badge>
                )}
              </div>
            )}
          </div>
          <div className="flex flex-col gap-2">
            <Button
              size="sm"
              onClick={() => setSelectedVideo(item)}
              data-testid={`video-view-${item.id}`}
            >
              <Play className="w-4 h-4 mr-2" />
              Play
            </Button>
            <Button variant="outline" size="sm">
              <Download className="w-4 h-4" />
            </Button>
          </div>
        </div>
      </CardContent>
    </Card>
  );

  return (
    <div className="container mx-auto p-6 space-y-6">
      <Header
        title="Video Evidence"
        subtitle="Video documentation and evidence files"
        onSearch={handleSearch}
        onExport={handleExport}
        searchPlaceholder="Search video evidence..."
      />

      {/* Video Stats */}
      <div className="grid grid-cols-2 lg:grid-cols-4 gap-6">
        <Card className="rounded-xl">
          <CardContent className="p-4 text-center">
            <div className="w-12 h-12 bg-chart-4/10 rounded-lg flex items-center justify-center mx-auto mb-3">
              <VideoIcon className="w-6 h-6 text-chart-4" />
            </div>
            <div className="text-2xl font-bold text-chart-4">{videoStats.total}</div>
            <div className="text-sm text-muted-foreground">Total Videos</div>
          </CardContent>
        </Card>

        <Card className="rounded-xl">
          <CardContent className="p-4 text-center">
            <div className="w-12 h-12 bg-primary/10 rounded-lg flex items-center justify-center mx-auto mb-3">
              <Play className="w-6 h-6 text-primary" />
            </div>
            <div className="text-2xl font-bold text-primary">{videoStats.hours}h</div>
            <div className="text-sm text-muted-foreground">Total Runtime</div>
          </CardContent>
        </Card>

        <Card className="rounded-xl">
          <CardContent className="p-4 text-center">
            <div className="w-12 h-12 bg-chart-2/10 rounded-lg flex items-center justify-center mx-auto mb-3">
              <Camera className="w-6 h-6 text-chart-2" />
            </div>
            <div className="text-2xl font-bold text-chart-2">{videoStats.evidence}</div>
            <div className="text-sm text-muted-foreground">Evidence Videos</div>
          </CardContent>
        </Card>

        <Card className="rounded-xl">
          <CardContent className="p-4 text-center">
            <div className="w-12 h-12 bg-destructive/10 rounded-lg flex items-center justify-center mx-auto mb-3">
              <VideoIcon className="w-6 h-6 text-destructive" />
            </div>
            <div className="text-2xl font-bold text-destructive">{videoStats.surveillance}</div>
            <div className="text-sm text-muted-foreground">Surveillance</div>
          </CardContent>
        </Card>
      </div>

      {/* Controls */}
      <Card className="rounded-xl">
        <CardContent className="p-4">
          <div className="flex flex-wrap items-center justify-between gap-4">
            <div className="flex items-center gap-4">
              <div className="flex items-center gap-2">
                <label className="text-sm font-medium">Quality:</label>
                <Select value={filterQuality} onValueChange={setFilterQuality}>
                  <SelectTrigger className="w-32" data-testid="video-quality-select">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="all">All Quality</SelectItem>
                    <SelectItem value="4k">4K</SelectItem>
                    <SelectItem value="1080p">1080p</SelectItem>
                    <SelectItem value="720p">720p</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <Input
                placeholder="Search video evidence..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="w-64"
                data-testid="video-search-input"
              />
            </div>

            <div className="flex items-center gap-2">
              <ObjectUploader
                maxNumberOfFiles={5}
                maxFileSize={524288000} // 500MB for video files
                allowedFileTypes={['.mp4', '.mov', '.avi', '.mkv', '.webm']}
                onGetUploadParameters={getUploadParameters}
                onComplete={handleUploadComplete}
                buttonClassName="gap-2"
              >
                <Upload className="w-4 h-4" />
                Upload Videos
              </ObjectUploader>

              <div className="flex items-center gap-1 border rounded-lg p-1">
                <Button
                  variant={viewMode === "grid" ? "default" : "ghost"}
                  size="sm"
                  onClick={() => setViewMode("grid")}
                  data-testid="video-grid-view"
                >
                  <Grid className="w-4 h-4" />
                </Button>
                <Button
                  variant={viewMode === "list" ? "default" : "ghost"}
                  size="sm"
                  onClick={() => setViewMode("list")}
                  data-testid="video-list-view"
                >
                  <List className="w-4 h-4" />
                </Button>
              </div>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Video Gallery */}
      {filteredVideos.length > 0 ? (
        <Card className="rounded-xl">
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <VideoIcon className="w-5 h-5" />
              Video Evidence ({filteredVideos.length})
            </CardTitle>
          </CardHeader>
          <CardContent>
            {viewMode === "grid" ? (
              <div className="grid md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
                {filteredVideos.map((video) => (
                  <Card
                    key={video.id}
                    className="rounded-xl cursor-pointer hover:shadow-lg transition-shadow"
                    onClick={() => setSelectedVideo(video)}
                    data-testid={`video-card-${video.id}`}
                  >
                    <CardContent className="p-4">
                      {renderVideoPreview(video)}
                      <div className="mt-3">
                        <h3 className="font-semibold text-sm mb-1 line-clamp-2">{video.title}</h3>
                        <p className="text-xs text-muted-foreground mb-2 line-clamp-2">{video.summary}</p>
                        <div className="flex items-center justify-between">
                          <Badge variant="secondary" className="text-xs">
                            {video.type.toUpperCase()}
                          </Badge>
                          <span className="text-xs text-muted-foreground">{video.date}</span>
                        </div>
                      </div>
                    </CardContent>
                  </Card>
                ))}
              </div>
            ) : (
              <div className="space-y-4">
                {filteredVideos.map(renderListView)}
              </div>
            )}
          </CardContent>
        </Card>
      ) : (
        <Card className="rounded-xl">
          <CardContent className="p-12 text-center">
            <VideoIcon className="w-16 h-16 mx-auto mb-4 text-muted-foreground/50" />
            <h3 className="text-lg font-medium mb-2">No video evidence found</h3>
            <p className="text-muted-foreground mb-4">
              {searchQuery 
                ? `No videos match your search for "${searchQuery}"`
                : "Upload video evidence to get started"
              }
            </p>
            <ObjectUploader
              maxNumberOfFiles={5}
              maxFileSize={524288000} // 500MB
              allowedFileTypes={['.mp4', '.mov', '.avi', '.mkv', '.webm']}
              onGetUploadParameters={getUploadParameters}
              onComplete={handleUploadComplete}
              buttonClassName="gap-2"
            >
              <Upload className="w-4 h-4" />
              Upload First Video
            </ObjectUploader>
          </CardContent>
        </Card>
      )}

      {/* Video Detail Modal */}
      {selectedVideo && (
        <Dialog open={true} onOpenChange={() => setSelectedVideo(null)}>
          <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto">
            <DialogHeader>
              <DialogTitle className="flex items-center gap-2">
                <Play className="w-5 h-5" />
                {selectedVideo.title}
              </DialogTitle>
            </DialogHeader>

            <div className="space-y-6">
              {/* Video Player Placeholder */}
              <div className="w-full h-64 bg-gradient-to-br from-chart-4/20 to-chart-1/20 flex items-center justify-center rounded-lg border">
                <div className="text-center">
                  <Play className="w-20 h-20 text-chart-4 mx-auto mb-4" />
                  <p className="text-lg font-medium text-chart-4">Video Player</p>
                  <p className="text-sm text-muted-foreground">Video playback would appear here</p>
                </div>
              </div>

              {/* Video Details */}
              <div className="grid md:grid-cols-2 gap-6">
                <div>
                  <h3 className="font-semibold mb-2">Details</h3>
                  <div className="space-y-2 text-sm">
                    <div><span className="font-medium">Type:</span> {selectedVideo.type}</div>
                    <div><span className="font-medium">Date:</span> {selectedVideo.date}</div>
                    <div><span className="font-medium">Source:</span> {selectedVideo.sourceNote}</div>
                  </div>
                </div>

                <div>
                  <h3 className="font-semibold mb-2">Tags</h3>
                  <div className="flex flex-wrap gap-2">
                    {selectedVideo.tags?.map((tag) => (
                      <Badge key={tag} variant="secondary" className="text-xs">
                        {tag}
                      </Badge>
                    ))}
                  </div>
                </div>
              </div>

              <div>
                <h3 className="font-semibold mb-2">Summary</h3>
                <p className="text-sm text-muted-foreground">{selectedVideo.summary}</p>
              </div>

              <div className="flex gap-3">
                <Button>
                  <Play className="w-4 h-4 mr-2" />
                  Play Video
                </Button>
                <Button variant="outline">
                  <Download className="w-4 h-4 mr-2" />
                  Download
                </Button>
              </div>
            </div>
          </DialogContent>
        </Dialog>
      )}
    </div>
  );
}

